xorW8 :: W8 -> W8 -> W8
xorW8 (W8 a0 a1 a2 a3 a4 a5 a6 a7) 
      (W8 b0 b1 b2 b3 b4 b5 b6 b7) = W8 (xb a0 b0)
                                     (xb a1 b1)
                                     (xb a2 b2)
                                     (xb a3 b3)
                                     (xb a4 b4)
                                     (xb a5 b5)
                                     (xb a6 b6)
                                     (xb a7 b7)

xor512 :: Bytes64 -> Bytes64 -> Bytes64
